import React, { Component } from 'react';
import { Card, Image, Icon, List, Header } from 'semantic-ui-react';

class ChampionTile extends Component {
  constructor(props) {
  super(props);
  }

  render(){
    const pathToIcon = require.context('../../../../public/champions', true);
    const pathToAbility = require.context('../../../../public/abilities', true);

    return (
      <List.Item className='icon-div'>
        <div className={'icon-' + this.props.champion.tier_color}>
          <div>
            <Card>
              <Image src={`${pathToIcon(this.props.champion.icon , true)}`} alt={`${this.props.champion.name}`} size='tiny' floated='left' />
              <Card.Content>
                <Card.Header>Matthew</Card.Header>
                <Card.Meta>
                  <span className='date'>Joined in 2015</span>
                </Card.Meta>
                <Card.Description>
                  Matthew is a musician living in Nashville.
                </Card.Description>
              </Card.Content>
              <Card.Content extra>
                <a>
                  <Icon name='user' />
                  22 Friends
                </a>
              </Card.Content>
            </Card>
          </div>
        </div>
      </List.Item>
    );
  }
}

export default ChampionTile;
