import React, { Component } from 'react';
import { browserHistory, Link } from 'react-router';
import { Responsive, Menu, Dropdown, Image, Segment, Search } from 'semantic-ui-react';

class NavigationBar extends Component {
  constructor(props) {
    super(props);
    this.state={
      home: false,
      standings: false
    }
  }

  componentWillMount() {
    switch(this.props.page) {
      case 'home':
        this.setState({ home: true })
        break;
      case 'standings':
        this.setState({ standings: true })
        break;
      default:
        break;
    }
  }

  render() {
    return (
      <div>
        <Responsive as={Menu} inverted fluid stackable widths='1' maxWidth={500}>
          <Dropdown item text={<Image src={require('../../../../public/favicon.ico')} />}>
            <Dropdown.Menu textAlign='center'>
              <Dropdown.Item text='Home' as={ Link } to='/' active={this.state.home} />
              <Dropdown.Item text='Standings' as={ Link } to='/champions/overview' active={this.state.standings} />
            </Dropdown.Menu>
          </Dropdown>
        </Responsive>

        <Responsive as={Menu} inverted fluid widths='2' size='small' minWidth={501}>
          <Menu.Item name='home' as={ Link } to='/' active={this.state.home}><Image src={require('../../../../public/favicon.ico')} size='mini' /></Menu.Item>
          <Menu.Item name='standings' as={ Link } to='/standings' active={this.state.standings}><p>Standings</p></Menu.Item>
        </Responsive>
      </div>
    )
  }
}

export default NavigationBar;
