import React, { Component } from 'react';
import { Image, List, Popup, Grid, Header } from 'semantic-ui-react';

class ChampionTile extends Component {
  constructor(props) {
  super(props);
  }

  render(){
    const pathToIcon = require.context('../../../../public/champions', true);
    const pathToAbility = require.context('../../../../public/abilities', true);

    return (
      <List.Item className='icon-div'>
        <div className={'icon-' + this.props.champion.tier_color}>
          <div>
          <Popup inverted={true} wide position='top center' hideOnScroll={true} basic={true} trigger={<Header><Image src={`${pathToIcon(this.props.champion.icon , true)}`} alt={`${this.props.champion.icon}`} avatar verticalAlign='middle' /><br />{this.props.champion.name}</Header>} >
            <Grid textAlign='center'>
              <Grid.Row>
                <Header>
                  <Image src={`${pathToIcon(this.props.champion.icon , true)}`} alt={`${this.props.champion.name}`} avatar verticalAlign='middle' />
                </Header>
              </Grid.Row>
              <Grid.Row>
                <span className='popup-description'>{this.props.champion.ability_description}</span>
              </Grid.Row>
            </Grid>
          </Popup>
          </div>
        </div>
      </List.Item>
    );
  }
}

export default ChampionTile;
