import React, { Component } from 'react';
import { Link } from 'react-router';
import { Menu, Container, Header, List, Dropdown, Message, Responsive, Image, Segment } from 'semantic-ui-react';

import NavigationBar from './NavigationBar';

class SummonerShow extends Component {
  constructor(props) {
    super(props);
    this.state={
      summoner: {},
      rank: {},
      updated: false
    }
  }

  componentDidMount() {
    let summonerName = this.props.params.name
    fetch(`/api/v1/summoners/${summonerName}`)
      .then(response => {
        if (response.ok) {
          return response;
        } else {
          let errorMessage = `${response.status}(${response.statusText})`;
          let error = new Error(errorMessage);
          throw(error);
        }
        })
      .then(response => response.json())
      .then(body => {
        this.setState({ summoner: body.summoner, rank: body.rank, updated: true })
      })
      .catch(error => console.error(`Error in fetch: ${error.message}`));
  }

  render() {

    const pathToRankedEmblem = require.context('../../../../public/emblems', true);

    let summonerDisplay;
    if (this.state.updated === true) {
      if (this.state.rank !== null) {
        let rankTier = `./${this.state.rank.tier}`;
        rankTier = rankTier.toLowerCase();
        summonerDisplay = (
          <span>
            <List>
              <List.Item>
                <Image id='summoner-show-page-icon' src={`http://ddragon.leagueoflegends.com/cdn/9.15.1/img/profileicon/${this.state.summoner.profileIconId}.png`} centered size='tiny' />
              </List.Item>
              <List.Item>
                <Image src={`${pathToRankedEmblem(rankTier, true)}`} alt={`${rankTier}`} centered size='tiny' />
                <span>{this.state.rank.tier} {this.state.rank.rank} {this.state.rank.leaguePoints}LP</span>
              </List.Item>
            </List>
            <span>{this.state.rank.wins} Wins</span>
            <span>{this.state.rank.wins + this.state.rank.losses} Games Played</span>
          </span>
        );
      } else if (this.state.summoner.status === undefined) {
        summonerDisplay = (
          <span>
            <Image id='summoner-show-page-icon' src={`http://ddragon.leagueoflegends.com/cdn/9.15.1/img/profileicon/${this.state.summoner.profileIconId}.png`} centered size='tiny' />
            <Image src='https://raw.communitydragon.org/9.15/game/assets/ux/mastery/mastery_icon_default.loadingscreenpolish.png' centered  size='tiny' />
            <Header.Subheader>Unranked</Header.Subheader>
          </span>
        );
      } else {
        summonerDisplay = (
          <Header.Subheader>This summoner doesn't exist!</Header.Subheader>
        );
      }
    }

    return (
      <div>
        <NavigationBar />

        <Segment id='summoner-show-page-container' textAlign='center'>
          <Header>{this.state.summoner.name}</Header>
          {summonerDisplay}
        </Segment>
      </div>
    )
  }
}

export default SummonerShow;
