import React, { Component } from 'react';
import { Link } from 'react-router';
import { Menu, Container, Header, List, Dropdown } from 'semantic-ui-react';

import ChampionTile from './ChampionTile';

class ChampionsClasses extends Component {
  constructor(props) {
    super(props);
    this.state={
      assassin: [],
      blademaster: [],
      brawler: [],
      elementalist: [],
      guardian: [],
      gunslinger: [],
      knight: [],
      ranger: [],
      shapeshifter: [],
      sorcerer: []
    }
  }

  componentDidMount() {
    fetch('/api/v1/champions/classes')
      .then(response => {
        if (response.ok) {
          return response;
        } else {
          let errorMessage = `${response.status}(${response.statusText})`;
          let error = new Error(errorMessage);
          throw(error);
        }
        })
      .then(response => response.json())
      .then(body => {
        this.setState({
          assassin: body.assassin,
          blademaster: body.blademaster,
          brawler: body.brawler,
          elementalist: body.elementalist,
          guardian: body.guardian,
          gunslinger: body.gunslinger,
          knight: body.knight,
          ranger: body.ranger,
          shapeshifter: body.shapeshifter,
          sorcerer: body.sorcerer
        })
      })
      .catch(error => console.error(`Error in fetch: ${error.message}`));
  }

  render() {
    this.state.demon.sort((a, b) => parseFloat(a.tier) - parseFloat(b.tier));
    let demon = this.state.demon.map(champion => {
      return (
        <ChampionTile
          key={champion.id}
          id={champion.id}
          champion={champion}
        />
      )
    });

    this.state.dragon.sort((a, b) => parseFloat(a.tier) - parseFloat(b.tier));
    let dragon = this.state.dragon.map(champion => {
      return (
        <ChampionTile
          key={champion.id}
          id={champion.id}
          champion={champion}
        />
      )
    });

    this.state.exile.sort((a, b) => parseFloat(a.tier) - parseFloat(b.tier));
    let exile = this.state.exile.map(champion => {
      return (
        <ChampionTile
          key={champion.id}
          id={champion.id}
          champion={champion}
        />
      )
    });

    this.state.glacial.sort((a, b) => parseFloat(a.tier) - parseFloat(b.tier));
    let glacial = this.state.glacial.map(champion => {
      return (
        <ChampionTile
          key={champion.id}
          id={champion.id}
          champion={champion}
        />
      )
    });

    this.state.imperial.sort((a, b) => parseFloat(a.tier) - parseFloat(b.tier));
    let imperial = this.state.imperial.map(champion => {
      return (
        <ChampionTile
          key={champion.id}
          id={champion.id}
          champion={champion}
        />
      )
