import React, { Component } from 'react';
import { Card, Image, Icon, List, Header, Grid, Transition, Dimmer, Segment } from 'semantic-ui-react';

class ChampionTile extends Component {
  constructor(props) {
    super(props);
    this.state={
      hovered: false,
      gridId: 'champion-column',
      cardId: 'champion-card',
      cardExtraId: 'champion-card-extra'
    }
    this.onHover = this.onHover.bind(this)
  }

  onHover(event){
    if(this.state.hovered === false) {
      this.setState({ hovered: true })
    } else {
      this.setState({
        hovered: false,
        gridId: 'champion-column',
        cardId: 'champion-card',
        cardExtraId: 'champion-card-extra'
      })
    }
  }

  render(){
    const pathToIcon = require.context('../../../../public/icons', true);
    const pathToSplashart = require.context('../../../../public/splasharts', true);
    const pathToAbility = require.context('../../../../public/abilities', true);

    return (
      <Grid.Column id={this.state.gridId} width='2'>
          <Card id={this.state.cardId}>
            <Image src={`${pathToSplashart(this.props.champion.icon , true)}`} onMouseEnter={event => this.onHover(event)} onMouseOut={event => this.onHover(event)} />
            <Card.Content extra id={this.state.cardExtraId}><Header as='p'>{this.props.champion.name}</Header></Card.Content>
          </Card>
      </Grid.Column>
    );
  }
}

export default ChampionTile;
