import React, { Component } from 'react';
import { Card, Image, Icon, List, Header, Grid, Transition, Dimmer } from 'semantic-ui-react';

class ChampionTile extends Component {
  constructor(props) {
    super(props);
    this.state={
      splashVisible: true,
      detailsVisible: false
    }
    this.setVisibility = this.setVisibility.bind(this)
  }

  setVisibility(event){
    if(this.state.splashVisible === true) {
      this.setState({ splashVisible: false, detailsVisible: true })
    } else {
      this.setState({ splashVisible: true, detailsVisible: false })
    }
  }

  render(){
    const pathToIcon = require.context('../../../../public/icons', true);
    const pathToSplashart = require.context('../../../../public/splasharts', true);
    const pathToAbility = require.context('../../../../public/abilities', true);

    return (
      <Grid.Column id='champion-column' width='2'>
        <Transition animation='tada' duration={500} visible={this.state.splashVisible}>
          <Card id='champion-card' onClick={event => this.setVisibility(event)} >
            <Dimmer.Dimmable
              as={<Image src={`${pathToSplashart(this.props.champion.icon , true)}`} />}
              dimmed={this.state.detailsVisible}>
            </Dimmer.Dimmable>
            <Card.Content extra id='champion-card-name'><Header as='p'>{this.props.champion.name}</Header></Card.Content>
          </Card>
        </Transition>
      </Grid.Column>
    );
  }
}

export default ChampionTile;
