import React, { Component } from 'react';
import { Table, Image, Popup, Header, Grid } from 'semantic-ui-react';

class ChampionsOverviewTile extends Component {
  constructor(props) {
  super(props);
  }

  render(){
    const pathToIcon = require.context('../../../../public/icons', true);
    const pathToSynergyIcon = require.context('../../../../public/synergies', true);

    let synergies = this.props.champion.teams.map(synergy => {
      return (
        <Popup on='click' inverted={true} wide position='top center' hideOnScroll={true} basic={true} trigger={<Header>        <Image key={synergy.id} src={`${pathToSynergyIcon(synergy.icon, true)}`} avatar /></Header>} >
          <Grid textAlign='center'>
            <Grid.Row>
              <Header>
                <Image src={`${pathToSynergyIcon(synergy.icon , true)}`} alt={`${synergy.name}`} avatar verticalAlign='middle' />
              </Header>
            </Grid.Row>
            <Grid.Row>
              <span id='synergy-popup-description'>{synergy.desciption}</span>
            </Grid.Row>
          </Grid>
        </Popup>
      )
    });

    return (
      <Table.Row>
        <Table.Cell><Image src={`${pathToIcon(this.props.champion.icon , true)}`} alt={`${this.props.champion.name}`} avatar /> {this.props.champion.name}</Table.Cell>
        <Table.Cell>{synergies}</Table.Cell>
        <Table.Cell collapsing>{this.props.champion.tier}</Table.Cell>
        <Table.Cell>{this.props.champion.ability_description}</Table.Cell>
      </Table.Row>
    );
  }
}

export default ChampionsOverviewTile;
