import React, { Component } from 'react';
import { Card, Image, Icon, List, Header } from 'semantic-ui-react';

class ChampionTile extends Component {
  constructor(props) {
  super(props);
  }

  render(){
    const pathToIcon = require.context('../../../../public/champions', true);
    const pathToAbility = require.context('../../../../public/abilities', true);

    return (
      <List.Item>
        <div className='champion-card'>
          <Card>
            <Image src={`${pathToIcon(this.props.champion.icon , true)}`} wrapped ui={false} />
            <Card.Content>
              <Card.Header>Daniel</Card.Header>
              <Card.Meta>Joined in 2016</Card.Meta>
              <Card.Description>
                Daniel is a comedian living in Nashville.
              </Card.Description>
            </Card.Content>
            <Card.Content extra>
              <a>
                <Icon name='user' />
                10 Friends
              </a>
            </Card.Content>
          </Card>
        </div>
      </List.Item>
    );
  }
}

export default ChampionTile;
